import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Budgets.css';
import data from '../../data.json';

function Budgets() {
  const [budgets, setBudgets] = useState([]);
  const [isAddingBudget, setIsAddingBudget] = useState(false);
  const [newBudget, setNewBudget] = useState({
    category: 'Entertainment',
    maxSpend: '',
    theme: 'green',
  });
  const [isEditingBudget, setIsEditingBudget] = useState(false);
  const [editingBudget, setEditingBudget] = useState(null);

  // Fetch budgets data from backend
  useEffect(() => {
    axios.get('/api/budgets') // Adjust to your API endpoint
      .then(response => {
        setBudgets(response.data);
      })
      .catch(error => {
        console.error('Error fetching budgets data', error);
      });
  }, []);

  // Handle adding a new budget
  const handleAddBudget = () => {
    axios.post('/api/budgets', newBudget) // Adjust to your API endpoint
      .then(response => {
        setBudgets([...budgets, response.data]);
        setNewBudget({ category: 'Entertainment', maxSpend: '', theme: 'green' });
        setIsAddingBudget(false);
      })
      .catch(error => {
        console.error('Error adding new budget', error);
      });
  };

  // Handle updating an existing budget
  const handleEditBudget = () => {
    axios.put(`/api/budgets/${editingBudget.id}`, editingBudget) // Adjust to your API endpoint
      .then(response => {
        setBudgets(budgets.map(budget => budget.id === editingBudget.id ? response.data : budget));
        setIsEditingBudget(false);
        setEditingBudget(null);
      })
      .catch(error => {
        console.error('Error updating budget', error);
      });
  };

  // Handle deleting a budget
  const handleDeleteBudget = (budgetId) => {
    axios.delete(`/api/budgets/${budgetId}`) // Adjust to your API endpoint
      .then(() => {
        setBudgets(budgets.filter(budget => budget.id !== budgetId));
      })
      .catch(error => {
        console.error('Error deleting budget', error);
      });
  };

  return (
    <div className="budgets-container">
      <h2>Budgets</h2>

      {/* Add New Budget Button */}
      {!isAddingBudget && (
        <button onClick={() => setIsAddingBudget(true)}>Add New Budget</button>
      )}

      {/* Add/Edit Budget Form */}
      {(isAddingBudget || isEditingBudget) && (
        <div className="budget-form">
          <h3>{isEditingBudget ? 'Edit Budget' : 'Add New Budget'}</h3>
          <label>Budget Category</label>
          <select
            value={isEditingBudget ? editingBudget.category : newBudget.category}
            onChange={(e) => isEditingBudget
              ? setEditingBudget({ ...editingBudget, category: e.target.value })
              : setNewBudget({ ...newBudget, category: e.target.value })}
          >
            <option value="Entertainment">Entertainment</option>
            <option value="Bills">Bills</option>
            <option value="Groceries">Groceries</option>
            <option value="Dining Out">Dining Out</option>
            <option value="Transportation">Transportation</option>
            <option value="Personal Care">Personal Care</option>
            <option value="Education">Education</option>
            <option value="Lifestyle">Lifestyle</option>
            <option value="Shopping">Shopping</option>
            <option value="General">General</option>
          </select>

          <label>Maximum Spend</label>
          <input
            type="number"
            value={isEditingBudget ? editingBudget.maxSpend : newBudget.maxSpend}
            onChange={(e) => isEditingBudget
              ? setEditingBudget({ ...editingBudget, maxSpend: e.target.value })
              : setNewBudget({ ...newBudget, maxSpend: e.target.value })}
          />

          <label>Theme</label>
          <select
            value={isEditingBudget ? editingBudget.theme : newBudget.theme}
            onChange={(e) => isEditingBudget
              ? setEditingBudget({ ...editingBudget, theme: e.target.value })
              : setNewBudget({ ...newBudget, theme: e.target.value })}
          >
            <option value="green">Green</option>
            <option value="yellow">Yellow</option>
            <option value="cyan">Cyan</option>
            <option value="navy">Navy</option>
            <option value="red">Red</option>
            <option value="purple">Purple</option>
            <option value="turquoise">Turquoise</option>
            <option value="brown">Brown</option>
            <option value="magenta">Magenta</option>
            <option value="blue">Blue</option>
            <option value="navy-grey">Navy Grey</option>
            <option value="army-green">Army Green</option>
            <option value="pink">Pink</option>
            <option value="gold">Gold</option>
            <option value="orange">Orange</option>
          </select>

          <button onClick={isEditingBudget ? handleEditBudget : handleAddBudget}>
            {isEditingBudget ? 'Save Changes' : 'Add Budget'}
          </button>
          <button onClick={() => {
            setIsAddingBudget(false);
            setIsEditingBudget(false);
            setEditingBudget(null);
          }}>Cancel</button>
        </div>
      )}

      {/* List of Budgets */}
      <div className="budget-list">
        {budgets.map(budget => (
          <div key={budget.id} className="budget">
            <h4>{budget.category}</h4>
            <p>Maximum Spend: ${budget.maxSpend}</p>
            <p>Theme: {budget.theme}</p>

            {/* Edit and Delete */}
            <button onClick={() => {
              setIsEditingBudget(true);
              setEditingBudget(budget);
            }}>Edit</button>
            <button onClick={() => handleDeleteBudget(budget.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Budgets;
